import { Component, Input } from '@angular/core';
import { Gif } from '../../interfaces/gifs.interfaces';

@Component({
  selector: 'gifs-card-list',
  templateUrl: './card-list.component.html',
})
export class CardListComponent {


  @Input()
  public gifs: Gif[] = [];

}
<div class="row">

  <div *ngFor="let gif of gifs" class="col-md-3 col-sm-6">

    <div class="card mb-2 text-center bg-dark">
      <!-- TODO: Crear imagen del gif -->
      <!-- Llaves cuadradas [src] remember: Decirle a Angular que este atributo/elemento de HTML es igual a una expresión de JS -->
      <img [src]="gif.images.downsized_medium.url" alt="">

      <div class="card-body text-light">
        <p class="card-text">     {{ gif.title }}   </p>
      </div>


    </div>
  </div>
</div>
