import { Component } from '@angular/core';

@Component({
  selector: 'gifs-search-box',
  template: `
    <h5>Buscar: </h5>
    <input type="text"
    class="form-control"
    placeholder="Buscar gifs..."
    (keypress.enter)="searchTag( txtTagInput.value )"
    #txtTagInput
    >

  `
})

export class SearchBoxComponent  {
    public searchTag( newTag: string) {
      console.log(newTag);
    }
}
