import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http'
//const GIPHY_API_KEY = 'EE3h2WcPP0wTZWqaevFaeQzJ0BtNCb70';

@Injectable({providedIn: 'root'})
export class GifsService {

  private apiKey: string = 'EE3h2WcPP0wTZWqaevFaeQzJ0BtNCb70';

  private _tagsHistory: string[] = [];

  constructor( private http: HttpClient ) { }

  get tagsHistory() {
    return [...this._tagsHistory];
  }

  searchTag( tag: string ):void  {
    if( tag.length === 0 ) return;
    this.organizeHistory(tag);

    this.http.get('https://api.giphy.com/v1/gifs/search?api_key=EE3h2WcPP0wTZWqaevFaeQzJ0BtNCb70&q=valorant&limit=10')

  }

  private organizeHistory(tag:string){
    tag = tag.toLowerCase();

    if (this.tagsHistory.includes(tag)) {
      this._tagsHistory = this._tagsHistory.filter( (oldTag) => oldTag !== tag );
    }

    this._tagsHistory.unshift(tag);
    this._tagsHistory = this.tagsHistory.splice(0,10);
  }
}
