import { Component, ViewChild } from '@angular/core';

@Component({
  selector: 'gifs-search-box',
  template: `
    <h5>Buscar: </h5>
    <input type="text"
    class="form-control"
    placeholder="Buscar gifs..."
    (keyup.enter)="searchTag( txtTagInput.value )"
    #txtTagInput
    >

  `
})

export class SearchBoxComponent  {

  @ViewChild()
  tagInput

    public searchTag( newTag: string) {
      console.log(newTag);
    }
}
