import { Component } from '@angular/core';
import { SearchBoxComponent } from 'src/app/gifs/components/search-box/search-box.component';
import { GifsService } from 'src/app/gifs/services/gifs.service';

@Component({
  selector: 'shared-sidebar',
  templateUrl: './sidebar.component.html',
  styleUrls: ['./sidebar.component.css']
})
export class SidebarComponent {

  constructor( private gifsService : GifsService , private searchBox : SearchBoxComponent ) { }

  get tags() {
   return this.gifsService.tagsHistory;
  }

  searchTag() {
    const newTag = this.searchBox.tagInput.nativeElement.value;

    this.gifsService.searchTag(newTag);
    this.searchBox.tagInput.nativeElement.value = '';
  }

}
<div>
  <div class="bg-dark border-right p-3" id="sidebar">
    <h3 class="text-light">GifsAPP</h3>
    <hr class="text-light">

    <div class="list-group list-reset">
      <button *ngFor="let tag of tags" class="list-group-item list-group-item-action">
        {{ tag | titlecase }}
      </button>
    </div>

  </div>
</div>
